$(document).ready(function() {
    $("[data-bss-tooltip]").tooltip();
  });
  
  (function($) {
    let $window = $(window),
      windowWidth = $window.width(),
      $body = $("body"),
      $sidebarToggle = $("#sidebarToggle"),
      $sidebar = $(".sidebar");
  
    function toggleSidebar() {
      if ($window.width() < 992 || !$sidebar.hasClass("collapsed")) {
        $body.animate({ "padding-left": "0" }, 100);
      } else if ($window.width() > 992 && $sidebar.hasClass("collapsed")) {
        $body.animate({ "padding-left": "14rem" }, 100);
      }
  
      if ($sidebar.hasClass("collapsed")) {
        $sidebar.removeClass("collapsed");
        $sidebar.fadeIn(100);
      } else {
        $sidebar.fadeOut(100, function() {
          $sidebarToggle.hide();
          $sidebar.addClass("collapsed");
          $sidebarToggle.fadeIn(100);
        });
      }
    }
  
    if ($window.width() < 992) {
      $sidebar.addClass("collapsed");
    }
  
    $sidebar.removeClass("mobile-hid");
    $sidebarToggle.click(toggleSidebar);
  
    $window.resize(function() {
      if (windowWidth !== $window.width()) {
        windowWidth = $window.width();
        if ((windowWidth < 992 && !$sidebar.hasClass("collapsed")) || (windowWidth > 992 && $sidebar.hasClass("collapsed"))) {
          toggleSidebar();
        }
      }
    });
  })(jQuery);  